require "curses";include $a=a=Curses;def w(i);i<5?i:5-i+4;end;$m="#### #  ###    ##    #   ";for j in 0..9;for i in 0..9;a.addstr $m[w(j)*5+w(i)].chr+(i>8?$/:"");end;end;$x=$y=2;def d(t="@");$a.setpos $y,$x;$a.addstr t;end;def m(x,y);$x,$y=x,y if $m[w(y)*5+w(x)]==32;end;s=a.init_screen;a.noecho;a.curs_set 0;s.keypad 1;d;until (c=s.getch)==?q;k={259=>[$x,$y-1],258=>[$x,$y+1],260=>[$x-1,$y],261=>[$x+1,$y]};d " ";m *k[c] if k[c];d;end